INSERT INTO `cms_article` VALUES ('113', '贾跃亭与甘薇申请离婚，破产后再添家庭破裂', '<p style=\"color:#222222;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\n	<span style=\"font-weight:700;\">南都讯 记者麻乐</span>“乐视”创始人贾跃亭日前正在美国申请个人破产重组，据网友爆料的相关文件显示，贾跃亭与其妻子甘薇于10月11日在成都市锦江区人民法院申请离婚，男方已支付51万美元（约合361万人民币）的赡养费。贾跃亭个人破产文件《Schedules of Assets and Liabilities and Statement of Financial Affairs》曝光，这份文件列出了贾跃亭目前所面临的诉讼情况，上面显示：“贾跃亭和其妻子甘薇于今年10月11日在成都市锦江区法院申请离婚，该案状态显示为‘审理中’。”甘薇与贾跃亭双方都尚未做出正面回应。\n</p>\n<img src=\"http://p3.pstatp.com/large/pgc-image/RcfYGUVAbqvyN4\" alt=\"贾跃亭与甘薇申请离婚，破产后再添家庭破裂\" /><img src=\"http://p9.pstatp.com/large/pgc-image/RfVPQs5HOpXyNd\" alt=\"贾跃亭与甘薇申请离婚，破产后再添家庭破裂\" />\n<div>\n	<br />\n</div>', 'bf9e87d1-13ca-4544-a961-58306080facb.jpg', '2', '8', null, '0', '1', '1', '0', '2019-10-22 11:24:28', '2019-10-22 11:23:28', null, '0', null, null);
