mapper.select:

select * from cms_article 

   
   
    title like "%" #{title} "%"
   
   
   
    and  channel_Id  =#{channelId}
   
    
   
    and  category_Id  =#{categoryId}
   
   
   
    and  status  =#{status}
   
   
    
    and  user_id  =#{userId}
   
   
     
   
    and  hot  =#{hot}
   




order by created desc
